/**
 * description: Sets a session query. This api handles all requests to fetch Phonebook results,
 *   database searches, etc. 
                     As an example. If the HMI wanted
 *   to fetch a list of all contacts. The filterID's' and filter's' will be empty,
 *   and the result type would be set to Required field list.
 * param: filterIDs: The previously communicated numeric identifiers of the different
 *   filter categories.
 * param: filters: The string filters which should be applied to the result.
 * param: resultType: The data which is expected from the query.
 * param: sessionID: Session ID obtained from OpenSession.
 * param: databaseType: Related database for this sessionquery.
 * param: RequiredField: List of required field , if default is set browsing item will be
 *   sent with default avlues based on config
 * param: resetPrevious: Whether to use old history or not.If true treat this as a new
 *   session query, do not use any old selection history.
 * param: AutoUpdateEnabled: This is applicable for result sets which might update
 *   dynamically,Call stack or Fav list
          							 
       returns:
 *   numResults: The number of records matching the search criteria.
      
 *   returns: SessionQueryID:The sessionQueryID allotted.This uniquely identifies a
 *   particular session query/filter for retrieving data in a session and it has to
 *   be passed 
                                for furthur setting the
 *   sessionQuery window and retrieving the contents of that window.
 */
template <typename ... _AttributeExtensions>
void PimDbHandlerProxy<_AttributeExtensions...>::setSessionQuery(const uint8_t &_sessionID, const ::v1::com::harman::btpres::BTPresTypes::eDatabaseType &_databaseType, const ::v1::com::harman::btpres::BTPresTypes::phonebookResultMap &_filterIDs, const ::v1::com::harman::btpres::BTPresTypes::filterMap &_filters, const ::v1::com::harman::btpres::BTPresTypes::requiredFieldMap &_RequiredField, const bool &_resetPrevious, const bool &_AutoUpdateEnabled, const bool &_AlphaJumpTable, CommonAPI::CallStatus &_internalCallStatus, ::v1::com::harman::btpres::BTPresTypes::eErrorType &_errorCode, uint32_t &_numResults, uint8_t &_sessionQueryID, uint8_t &_unreadCount, ::v1::com::harman::btpres::BTPresTypes::alphaJumpList &_alphaList, const CommonAPI::CallInfo *_info) {
    if (!_databaseType.validate()) {
        _internalCallStatus = CommonAPI::CallStatus::INVALID_VALUE;
        return;
    }
    delegate_->setSessionQuery(_sessionID, _databaseType, _filterIDs, _filters, _RequiredField, _resetPrevious, _AutoUpdateEnabled, _AlphaJumpTable, _internalCallStatus, _errorCode, _numResults, _sessionQueryID, _unreadCount, _alphaList, _info);
}

template <typename ... _AttributeExtensions>
std::future<CommonAPI::CallStatus> PimDbHandlerProxy<_AttributeExtensions...>::setSessionQueryAsync(const uint8_t &_sessionID, const ::v1::com::harman::btpres::BTPresTypes::eDatabaseType &_databaseType, const ::v1::com::harman::btpres::BTPresTypes::phonebookResultMap &_filterIDs, const ::v1::com::harman::btpres::BTPresTypes::filterMap &_filters, const ::v1::com::harman::btpres::BTPresTypes::requiredFieldMap &_RequiredField, const bool &_resetPrevious, const bool &_AutoUpdateEnabled, const bool &_AlphaJumpTable, SetSessionQueryAsyncCallback _callback, const CommonAPI::CallInfo *_info) {
    if (!_databaseType.validate()) {
        ::v1::com::harman::btpres::BTPresTypes::eErrorType errorCode = ::v1::com::harman::btpres::BTPresTypes::eErrorType::ERROR_TYPE_SUCCESS;
        uint32_t numResults = 0ul;
        uint8_t sessionQueryID = 0u;
        uint8_t unreadCount = 0u;
        ::v1::com::harman::btpres::BTPresTypes::alphaJumpList alphaList = {};
        _callback(CommonAPI::CallStatus::INVALID_VALUE, errorCode, numResults, sessionQueryID, unreadCount, alphaList);
        std::promise<CommonAPI::CallStatus> promise;
        promise.set_value(CommonAPI::CallStatus::INVALID_VALUE);
        return promise.get_future();
    }
    return delegate_->setSessionQueryAsync(_sessionID, _databaseType, _filterIDs, _filters, _RequiredField, _resetPrevious, _AutoUpdateEnabled, _AlphaJumpTable, _callback, _info);
}
